      onClick={() => setChecked(!checked)}
      >
      <input type='checkbox' 
      className='w-[25px] h-[26px] bg-white border border-[#D9D9D9]
        border-[5px]'
        checked={checked}
      />
      <div className='title_garant'>{service}</div>
